package myproject;

public class Pattern3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=1;i<=5;i++)
		{
			for(int j=1;j<=5-i;j++)
			{
				
					System.out.print(" ");
				}
				for(int l=1;l<=i;l++)
				{
					System.out.print
					(l);
				}
				for(int k=i-1;k>0;k--)
				{
				
				System.out.print ( k);
			}
		System.out.println();
		}
		}
			
}
